namespace BgCommon.DbService.Models.Entities;/// <summary>/// 权限设定列表.<br/>/// 权限通常是定义好的、固定的操作，例如 "User.Create", "User.Delete"/// </summary>public partial class Permission :    ObservableValidator,    ICloneable,    ISelfValidator{    /// <summary>    /// Gets or sets 权限ID.    /// </summary>    [Key]    public int Id { get; set; }    /// <summary>    /// 此权限所属的模块ID (外键)    /// 可以为 null，表示这是一个不与任何UI模块直接绑定的系统级权限    /// </summary>    [ObservableProperty]    private long? moduleId = null;    /// <summary>    /// Gets or sets 权限名称.    /// 例如："创建用户"    /// </summary>    [Required(AllowEmptyStrings = false, ErrorMessage = "名称不能为空")]    [MaxLength(100)]    [ObservableProperty]    private string name = string.Empty;    /// <summary>    /// 例如："User.Create", 用于代码中判断    /// </summary>    [Required(AllowEmptyStrings = false, ErrorMessage = "唯一编码不能为空")]    [MaxLength(100)]    [ObservableProperty]    private string code = string.Empty;    /// <summary>    /// Gets or sets 权限描述.    /// </summary>    [MaxLength(200)]    [ObservableProperty]    private string description = string.Empty;    /// <summary>    /// Gets or sets 导航属性：指向所属模块    /// 在这里添加 [ForeignKey] 特性    /// 它告诉EF Core: "下面这个 ModuleInfo 导航属性，    /// 是通过我指定的 'moduleId' 这个属性作为外键来关联的。    /// </summary>    [ForeignKey(nameof(ModuleId))]    public virtual ModuleInfo? Module { get; set; }    /// <summary>    /// Gets or sets 导航属性：通过 RolePermission 连接表指向多个角色.    /// </summary>    public virtual ICollection<RolePermission> RolePermissions { get; set; } = new List<RolePermission>();    public object Clone()    {        return this.MemberwiseClone();    }    public void Validate()    {        this.ValidateAllProperties();    }}