namespace BgCommon.DbService;/// <summary>/// 定义了通用的、与具体业务无关的基础权限代码./// 这些枚举成员通常用作权限标识符的后缀或一部分./// </summary>[TypeConverter(typeof(EnumLocalizationConverter))]public enum PermissionCode{    /// <summary>    /// 查看操作.    /// </summary>    [Display(Name = "查看")]    View,    /// <summary>    /// 新增操作.    /// </summary>    [Display(Name = "新增")]    Create,    /// <summary>    /// 编辑操作.    /// </summary>    [Display(Name = "编辑")]    Edit,    /// <summary>    /// 删除操作.    /// </summary>    [Display(Name = "删除")]    Delete,    /// <summary>    /// 保存操作.    /// </summary>    [Display(Name = "保存")]    Save,    /// <summary>    /// 复制操作.    /// </summary>    [Display(Name = "复制")]    Copy,    /// <summary>    /// 重命名操作.    /// </summary>    [Display(Name = "重命名")]    Rename,    /// <summary>    /// 导入操作.    /// </summary>    [Display(Name = "导入")]    Import,    /// <summary>    /// 导出操作.    /// </summary>    [Display(Name = "导出")]    Export,    /// <summary>    /// 打印操作.    /// </summary>    [Display(Name = "打印")]    Print,}