using BgCommon.DbService.Models.Entities;using Prism.Common;namespace BgCommon.DbService.Services;/// <summary>/// 显示模块服务./// </summary>public interface IModuleViewService{    /// <summary>    /// 请求导航到指定模块.    /// </summary>    /// <param name="regionName">区域名称.</param>    /// <param name="currentUser">当前用户信息.</param>    /// <param name="moduleCode">模块代码.</param>    /// <param name="permissionCode">模块查看权限编码.</param>    /// <param name="configura">导航参数配置委托.</param>    /// <param name="callBack">导航结果回调委托.</param>    /// <returns>操作结果.</returns>    Task RequestNavigateAsync(        string regionName,        UserInfo? currentUser,        string moduleCode,        string? permissionCode = null,        Action<INavigationParameters>? configura = null,        Action<IParameters>? callBack = null);    /// <summary>    /// 异步显示模块视图.    /// </summary>    /// <param name="currentUser">当前用户信息.</param>    /// <param name="moduleCode">模块代码.</param>    /// <param name="permissionCode">模块查看权限编码.</param>    /// <param name="dialogWindowName">对话框窗口名称.</param>    /// <param name="configuration">对话框参数配置委托.</param>    /// <returns>操作结果.</returns>    Task<ResponseResult<IDialogResult>> ShowViewAsync(        UserInfo? currentUser,        string moduleCode,        string? permissionCode = null,        string? dialogWindowName = null,        Action<IDialogParameters>? configuration = null);    /// <summary>    /// 异步显示对话框视图.    /// </summary>    /// <param name="currentUser">当前用户信息.</param>    /// <param name="moduleCode">模块代码.</param>    /// <param name="permissionCode">模块查看权限编码.</param>    /// <param name="dialogWindowName">对话框窗口名称.</param>    /// <param name="configuration">对话框参数配置委托.</param>    /// <returns>包含对话框操作结果的操作结果.</returns>    Task<ResponseResult<IDialogResult>> ShowDialogViewAsync(        UserInfo? currentUser,        string moduleCode,        string? permissionCode = null,        string? dialogWindowName = null,        Action<IDialogParameters>? configuration = null);}