namespace BgCommon.Localization;/// <summary>/// 本地化提供者接口，提供检索和管理特定区域性的本地化资源的功能./// </summary>public interface ILocalizationProvider{    /// <summary>    /// 检索具有指定区域性的本地化集列表.    /// </summary>    /// <param name="culture">区域性信息对象，表示要检索的本地化资源的文化.</param>    /// <returns>包含指定区域性的所有本地化集的列表.</returns>    IEnumerable<LocalizationSet> GetLocalizationSets(CultureInfo culture);    /// <summary>    /// 检索具有指定区域性和名称的本地化集.    /// </summary>    /// <param name="culture">区域性信息对象，表示要检索的本地化资源的文化.</param>    /// <param name="name">要获取的本地化集的名称，可空.</param>    /// <returns>具有指定区域性和名称的本地化集，如果找不到则为null.</returns>    LocalizationSet? GetLocalizationSet(CultureInfo culture, string? name);    /// <summary>    /// 获取当前使用的区域性信息.    /// </summary>    /// <returns>表示当前文化的CultureInfo对象.</returns>    CultureInfo GetCulture();    /// <summary>    /// 设置当前使用的区域性信息.    /// </summary>    /// <param name="cultureInfo">要设置为当前文化的CultureInfo对象.</param>    void SetCulture(CultureInfo cultureInfo);    /// <summary>    /// 检索指定关键字的本地化字符串，使用当前区域性.    /// </summary>    /// <param name="key">本地化资源的关键字.</param>    /// <param name="assembleyName">程序集名称，指定从哪个程序集加载资源，可空.</param>    /// <returns>本地化后的字符串.</returns>    string GetString(string key, string? assembleyName = null);    /// <summary>    /// 检索指定关键字和区域性的本地化字符串.<br/>    /// 支持不同模块中使用相同的多语言关键字，但不支持同一模块内关键字重复.<br/>    /// 调用此方法时，默认只加载调用程序集的资源文件.    /// </summary>    /// <param name="key">本地化资源的关键字.</param>    /// <param name="cultureInfo">区域性信息对象，表示要检索的本地化资源的文化.</param>    /// <param name="assembleyName">程序集名称，指定从哪个程序集加载资源，可空.</param>    /// <returns>本地化后的字符串.</returns>    string GetString(string key, CultureInfo cultureInfo, string? assembleyName = null);    /// <summary>    /// 检索指定关键字在多个不同区域性下的本地化字符串.<br/>    /// 支持不同模块中使用相同的多语言关键字，但不支持同一模块内关键字重复.<br/>    /// 调用此方法时，默认只加载调用程序集的资源文件.    /// </summary>    /// <param name="key">本地化资源的关键字.</param>    /// <param name="assembleyName">程序集名称，指定从哪个程序集加载资源，可空.</param>    /// <param name="cultureInfos">区域性信息对象数组，表示要检索的多个本地化资源的文化.</param>    /// <returns>包含各区域性本地化字符串的数组，顺序与传入的cultureInfos参数一致.</returns>    string[] GetStrings(string key, string? assembleyName, params CultureInfo[] cultureInfos);}