namespace BgCommon.Prism.Wpf.Authority.Entities;/// <summary>/// 用户信息./// </summary>public partial class UserInfo : ObservableValidator{    /// <summary>    /// Gets or sets 用户ID (主键).    /// </summary>    [Key]    public long Id { get; set; }    /// <summary>    /// Gets or sets 用户名.    /// </summary>    [Required]    [MaxLength(50)]    [ObservableProperty]    private string userName = string.Empty;    /// <summary>    /// Gets or sets 密码.    /// </summary>    [Required]    [MaxLength(50)]    [ObservableProperty]    private string password = string.Empty;    /// <summary>    /// Gets or sets 创建时间.    /// </summary>    [Required]    [ObservableProperty]    private DateTime createdAt = DateTime.Now;    /// <summary>    /// Gets or sets 是否在线.    /// </summary>    [Required]    [ObservableProperty]    private bool isOnline = false;    /// <summary>    /// Gets or sets 是否启用.    /// </summary>    [Required]    [ObservableProperty]    private bool isActive = true;    /// <summary>    /// Gets or sets 导航属性：一个用户可以有多个角色    /// </summary>    public ICollection<UserRole> UserRoles { get; set; } = new List<UserRole>();    public UserInfo() { }    public UserInfo(string userName, string password)    {        UserName = userName;        Password = password;        CreatedAt = DateTime.Now;        IsActive = true;    }    public UserInfo(UserInfo other)    {        Id = other.Id;        UserName = other.UserName;        Password = other.Password;        CreatedAt = other.CreatedAt;        IsOnline = other.IsOnline;        IsActive = other.IsActive;        UserRoles = other.UserRoles;    }}