namespace BgCommon.Prism.Wpf.Authority.Models;/// <summary>/// 登录结果/// </summary>/// <typeparam name="T">结果集数据类型</typeparam>public class AuthorityResult<T> : AuthorityResult{    /// <summary>    /// Gets or sets 返回的结果集    /// </summary>    public new T? Result { get; set; }    public static AuthorityResult<T> ToSuccess(int code = 0, string message = "操作成功", T? data = default(T))        => new() { Code = code, Success = true, Message = message, Result = data };    public static new AuthorityResult<T> ToFail(string message)        => new() { Code = -1, Success = false, Message = message };}/// <summary>/// 登录结果/// </summary>public class AuthorityResult{    /// <summary>    /// Gets or sets 错误码.<br/>    /// 0 : 取消登录<br/>    /// 1 : 登录成功<br/>    /// -1 ：登录失败<br/>    /// </summary>    public int Code { get; set; }    /// <summary>    /// Gets or sets a value indicating whether 是否登录成功    /// </summary>    public bool Success { get; set; }    /// <summary>    /// Gets or sets 返回的结果集    /// </summary>    public object? Result { get; set; }    /// <summary>    /// Gets or sets 提示信息    /// </summary>    public string Message { get; set; } = string.Empty;    public static AuthorityResult ToSuccess(int code = 0, string message = "操作成功", object? data = null)        => new() { Code = code, Success = true, Message = message, Result = data };    public static AuthorityResult ToFail(string message)        => new() { Code = -1, Success = false, Message = message };}