namespace BgCommon;/// <summary>/// 公共扩展./// </summary>public static partial class Extensions{    /// <summary>    /// 获取类型的全名，包括程序集信息.    /// </summary>    /// <param name="type">指定类型.</param>    /// <returns>返回类型的全名，包括程序集信息.</returns>    public static string ToFullName(this Type type)    {        return $"{type.FullName},{type.Assembly.GetName().Name}";    }    /// <summary>    /// 安全获取值，当值为 null时，不会抛出异常.    /// </summary>    /// <typeparam name="T">数据类型.</typeparam>    /// <param name="value">可空值.</param>    public static T SafeValue<T>(this T? value)        where T : struct    {        return value ?? default;    }    /// <summary>    /// 转换为用分隔符连接的字符串.    /// </summary>    /// <typeparam name="T">集合元素类型.</typeparam>    /// <param name="list">集合.</param>    /// <param name="quotes">引号，默认不带引号，范例：单引号 "'".</param>    /// <param name="separator">分隔符，默认使用逗号分隔.</param>    internal static string Join<T>(this IEnumerable<T> list, string quotes = "", string separator = ",")    {        return Join(list, quotes, separator);    }}