namespace BgLogger;/// <summary>/// 日志记录接口，定义了静态日志记录方法的契约./// </summary>public interface IBgLogger{    /// <summary>    /// 定义记录 Trace 级别日志的静态抽象方法.    /// </summary>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    public static abstract void Trace(string format, params object[] args);    /// <summary>    /// 定义记录带异常信息的 Trace 级别日志的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    public static abstract void Trace(Exception ex, string format, params object[] args);    /// <summary>    /// 定义记录 Trace 级别异常信息的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    public static abstract void Trace(Exception ex);    /// <summary>    /// 定义记录 Debug 级别日志的静态抽象方法.    /// </summary>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    public static abstract void Debug(string format, params object[] args);    /// <summary>    /// 定义记录带异常信息的 Debug 级别日志的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    public static abstract void Debug(Exception ex, string format, params object[] args);    /// <summary>    /// 定义记录 Debug 级别异常信息的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    public static abstract void Debug(Exception ex);    /// <summary>    /// 定义记录 Info 级别日志的静态抽象方法.    /// </summary>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    public static abstract void Info(string format, params object[] args);    /// <summary>    /// 定义记录带异常信息的 Info 级别日志的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    public static abstract void Info(Exception ex, string format, params object[] args);    /// <summary>    /// 定义记录 Info 级别异常信息的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    public static abstract void Info(Exception ex);    /// <summary>    /// 定义记录 Warn 级别日志的静态抽象方法.    /// </summary>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    public static abstract void Warn(string format, params object[] args);    /// <summary>    /// 定义记录带异常信息的 Warn 级别日志的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    public static abstract void Warn(Exception ex, string format, params object[] args);    /// <summary>    /// 定义记录 Warn 级别异常信息的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    public static abstract void Warn(Exception ex);    /// <summary>    /// 定义记录 Error 级别日志的静态抽象方法.    /// </summary>    /// <param name="format">日志消息 或格式化日志消息.</param>    /// <param name="args">格式化参数.</param>    public static abstract void Error(string format, params object[] args);    /// <summary>    /// 定义记录带异常信息的 Error 级别日志的静态抽象方法.    /// </summary>    /// <param name="ex">异常对象（可选）.</param>    /// <param name="format">日志消息 或格式化日志消息.</param>    /// <param name="args">格式化参数.</param>    public static abstract void Error(Exception ex, string format, params object[] args);    /// <summary>    /// 定义记录 Error 级别异常信息的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    public static abstract void Error(Exception ex);    /// <summary>    /// 定义记录 Fatal 级别日志的静态抽象方法.    /// </summary>    /// <param name="format">日志消息 或格式化日志消息.</param>    /// <param name="args">格式化参数.</param>    public static abstract void Fatal(string format, params object[] args);    /// <summary>    /// 定义记录带异常信息的 Fatal 级别日志的静态抽象方法.    /// </summary>    /// <param name="ex">异常对象（可选）.</param>    /// <param name="format">日志消息 或格式化日志消息.</param>    /// <param name="args">格式化参数.</param>    public static abstract void Fatal(Exception ex, string format, params object[] args);    /// <summary>    /// 定义记录 Fatal 级别异常信息的静态抽象方法.    /// </summary>    /// <param name="ex">要记录的异常.</param>    public static abstract void Fatal(Exception ex);}