namespace BgCommon.Localization;/// <summary>/// BgCommon.Localization 内部使用./// </summary>class Logger{    private static Lazy<NLog.Logger> logger = new Lazy<NLog.Logger>(() => NLog.LogManager.GetLogger("General"));    /// <summary>    /// 记录 Trace 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    internal static void Trace(string format, params object[] args)    {        logger.Value?.Trace(CultureInfo.CurrentCulture, format, args);    }    /// <summary>    /// 记录带异常信息的 Trace 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    internal static void Trace(Exception ex, string format, params object[] args)    {        logger.Value?.Trace(ex, format, args);    }    /// <summary>    /// 记录 Trace 级别的异常信息（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    internal static void Trace(Exception ex)    {        logger.Value?.Trace(ex);    }    /// <summary>    /// 记录 Debug 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    internal static void Debug(string format, params object[] args)    {        logger.Value?.Debug(CultureInfo.CurrentCulture, format, args);    }    /// <summary>    /// 记录带异常信息的 Debug 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    internal static void Debug(Exception ex, string format, params object[] args)    {        logger.Value?.Debug(ex, format, args);    }    /// <summary>    /// 记录 Debug 级别的异常信息（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    internal static void Debug(Exception ex)    {        logger.Value?.Debug(ex);    }    /// <summary>    /// 记录 Info 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    internal static void Info(string format, params object[] args)    {        logger.Value?.Info(CultureInfo.CurrentCulture, format, args);    }    /// <summary>    /// 记录带异常信息的 Info 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    internal static void Info(Exception ex, string format, params object[] args)    {        logger.Value?.Info(ex, format, args);    }    /// <summary>    /// 记录 Info 级别的异常信息（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    internal static void Info(Exception ex)    {        logger.Value?.Info(ex);    }    /// <summary>    /// 记录 Warn 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    internal static void Warn(string format, params object[] args)    {        logger.Value?.Warn(CultureInfo.CurrentCulture, format, args);    }    /// <summary>    /// 记录带异常信息的 Warn 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    /// <param name="format">带格式项的日志消息.</param>    /// <param name="args">格式化参数数组.</param>    internal static void Warn(Exception ex, string format, params object[] args)    {        logger.Value?.Warn(ex, format, args);    }    /// <summary>    /// 记录 Warn 级别的异常信息（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    internal static void Warn(Exception ex)    {        logger.Value?.Warn(ex);    }    /// <summary>    /// 记录 Error 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="format">日志消息 或格式化日志消息.</param>    /// <param name="args">格式化参数.</param>    internal static void Error(string format, params object[] args)    {        logger.Value?.Error(CultureInfo.CurrentCulture, format, args);    }    /// <summary>    /// 记录 Error 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">异常对象（可选）.</param>    /// <param name="format">日志消息 或格式化日志消息.</param>    /// <param name="args">格式化参数.</param>    internal static void Error(Exception ex, string format, params object[] args)    {        logger.Value?.Error(ex, format, args);    }    /// <summary>    /// 记录 Error 级别的异常信息（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    internal static void Error(Exception ex)    {        logger.Value?.Error(ex);    }    /// <summary>    /// 记录 Fatal 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="format">日志消息 或格式化日志消息.</param>    /// <param name="args">格式化参数.</param>    internal static void Fatal(string format, params object[] args)    {        logger.Value?.Fatal(CultureInfo.CurrentCulture, format, args);    }    /// <summary>    /// 记录 Fatal 级别日志（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">异常对象（可选）.</param>    /// <param name="format">日志消息 或格式化日志消息.</param>    /// <param name="args">格式化参数.</param>    internal static void Fatal(Exception ex, string format, params object[] args)    {        logger.Value?.Fatal(ex, format, args);    }    /// <summary>    /// 记录 Fatal 级别的异常信息（基于 LogSource 枚举）.    /// </summary>    /// <param name="ex">要记录的异常.</param>    internal static void Fatal(Exception ex)    {        logger.Value?.Fatal(ex);    }}