namespace BgCommon.Localization;/// <summary>/// Provides extension methods for the <see cref="Application"/> class./// </summary>public static class ApplicationExtensions{    /// <summary>    /// Configures the application to use a string localizer.    /// </summary>    /// <param name="app">The application to configure.</param>    /// <param name="configure">A delegate to configure the localization builder.</param>    /// <returns>The configured application.</returns>    public static Application UseStringLocalizer(this Application app, Action<LocalizationBuilder> configure)    {        LocalizationBuilder builder = new();        configure(builder);        LocalizationProviderFactory.SetInstance(builder.Build());        return app;    }    /// <summary>    /// Sets the culture for localization in the application.    /// </summary>    /// <param name="app">The application to set the culture for.</param>    /// <param name="culture">The culture to set.</param>    /// <returns>The application with the set culture.</returns>    public static Application SetLocalizationCulture(this Application app, CultureInfo culture)    {        CultureInfo.CurrentUICulture = culture;        CultureInfo.DefaultThreadCurrentUICulture = culture;        LocalizationProviderFactory.GetInstance()?.SetCulture(culture);        return app;    }}