using System.Windows.Media;using RoslynPad.Roslyn.Completion;namespace RoslynPad.Roslyn;public static class GlyphExtensions{    public static IGlyphService GlyphService { get; set; } = new DefaultGlyphService();    public static ImageSource? ToImageSource(this Glyph glyph) => GlyphService.GetGlyphImage(glyph) as DrawingImage;    private class DefaultGlyphService : IGlyphService    {        public object? GetGlyphImage(Glyph glyph)        {            // 使用 Application.Current.FindResource 来查找资源。            // FindResource 会在整个应用程序的资源树中查找，比直接访问 Resources 集合更可靠。            string glyphKey = glyph.ToString();            object? glyphImage = Application.Current.FindResource(glyphKey);            if (glyphImage is Drawing drawing)            {                return new DrawingImage { Drawing = drawing };            }            return null;        }    }}