namespace BgCommon.Script;/// <summary>/// 表示脚本模板的配置信息类./// </summary>public class ScriptTemplate{    /// <summary>    /// Initializes a new instance of the <see cref="ScriptTemplate"/> class.    /// </summary>    /// <param name="templateName">脚本模板的名称.</param>    /// <param name="templatePath">脚本模板文件的路径.</param>    /// <param name="referLibsPath">依赖库所在的根路径.</param>    /// <param name="referLibs">需要引用的程序集名称列表.</param>    public ScriptTemplate(string templateName, string templatePath, string referLibsPath, params string[] referLibs)    {        // 验证参数是否为空.        ArgumentNullException.ThrowIfNull(templateName, nameof(templateName));        ArgumentNullException.ThrowIfNull(templatePath, nameof(templatePath));        ArgumentNullException.ThrowIfNull(referLibsPath, nameof(referLibsPath));        ArgumentNullException.ThrowIfNull(referLibs, nameof(referLibs));        // 为属性赋值，显式使用 this 标识.        this.TemplateName = templateName;        this.TemplatePath = templatePath;        this.ReferLibsPath = referLibsPath;        // 初始化引用库列表.        this.ReferLibs = new List<string>(referLibs);    }    /// <summary>    /// Gets 模板名称.    /// </summary>    public string TemplateName { get; }    /// <summary>    /// Gets 模板所在路径.    /// </summary>    public string TemplatePath { get; }    /// <summary>    /// Gets 引用程序集所在路径.    /// </summary>    public string ReferLibsPath { get; }    /// <summary>    /// Gets 模板引用的程序集列表.    /// </summary>    /// <remarks>    /// 例如： "System.Xml.dll" 或 "3th\BgCommon.dll" .    /// </remarks>    public List<string> ReferLibs { get; } = new List<string>();}