namespace BgControls.Windows.Controls;/// <summary>/// 对话框正在关闭时的事件处理程序委托./// </summary>/// <param name="sender">事件发送者.</param>/// <param name="eventArgs">对话框正在关闭的事件参数.</param>public delegate void DialogClosingEventHandler(object sender, DialogClosingEventArgs eventArgs);/// <summary>/// 对话框正在关闭时的事件参数./// </summary>public class DialogClosingEventArgs : RoutedEventArgs{    /// <summary>    /// Initializes a new instance of the <see cref="DialogClosingEventArgs"/> class.    /// </summary>    /// <param name="session">对话框会话.</param>    /// <param name="routedEvent">路由事件.</param>    public DialogClosingEventArgs(DialogSession session, RoutedEvent routedEvent)        : base(routedEvent)    {        ArgumentNullException.ThrowIfNull(session, nameof(session));        this.Session = session;    }    /// <summary>    /// Gets a value indicating whether 对话框关闭操作是否已被取消.    /// </summary>    public bool IsCancelled { get; private set; }    /// <summary>    /// Gets 最初提供给 <see cref="CloseDialogCommand" /> 的参数.    /// </summary>    public object? Parameter => this.Session.CloseParameter;    /// <summary>    /// Gets 当前的对话框会话.    /// </summary>    public DialogSession Session { get; }    /// <summary>    /// 取消对话框的关闭操作.    /// </summary>    public void Cancel()    {        // 将取消标记设置为 true.        this.IsCancelled = true;    }}