namespace BgControls.Windows.Controls;/// <summary>/// 消息条消息类型转换器，用于将字符串等类型转换为 <see cref="SnackbarMessage"/> 对象./// </summary>public class SnackbarMessageTypeConverter : TypeConverter{    /// <summary>    /// 确定此转换器是否可以将指定类型的对象转换为此转换器的类型.    /// </summary>    /// <param name="context">类型说明符上下文.</param>    /// <param name="sourceType">要转换的源类型.</param>    /// <returns>如果可以转换则返回 true；否则返回 false.</returns>    public override bool CanConvertFrom(ITypeDescriptorContext? context, Type sourceType)    {        // 检查源类型是否不是字符串类型.        if (sourceType != typeof(string))        {            // 如果不是字符串，则调用基类方法判断是否可转换.            return base.CanConvertFrom(context, sourceType);        }        // 字符串类型默认可以转换.        return true;    }    /// <summary>    /// 将指定值转换为此转换器的类型.    /// </summary>    /// <param name="context">类型说明符上下文.</param>    /// <param name="culture">区域性信息.</param>    /// <param name="value">要转换的对象值.</param>    /// <returns>转换后的 <see cref="SnackbarMessage"/> 对象.</returns>    public override object? ConvertFrom(ITypeDescriptorContext? context, CultureInfo? culture, object value)    {        // 尝试将输入值识别为字符串.        if (value is string sourceString)        {            // 创建并返回一个新的 SnackbarMessage 实例，并设置其内容.            return new SnackbarMessage            {                Content = sourceString,            };        }        // 如果不是字符串，则尝试使用基类转换逻辑.        return base.ConvertFrom(context, culture, value);    }}