namespace ToolkitDemo;public class ShowSplashEvent : PubSubEvent<(double, string, string)>{}public static class EventAggregatorExtensions{    public static void Report<TContainer>(this TContainer container, double progress, string title, string content)        where TContainer : IContainerRegistry    {        try        {            if (container is IContainerExtension container1)            {                IEventAggregator eventAggregator = container1.Resolve<IEventAggregator>();                eventAggregator.Report(progress, title, content);            }        }        catch (Exception)        {            throw;        }    }    public static void Report(this IEventAggregator eventAggregator, double progress, string title, string content)    {        eventAggregator.GetEvent<ShowSplashEvent>().Publish((progress, title, content));    }}