using System.Windows;namespace XceedLicenseGenerator;/// <summary>Class that registers the classes defined in the WPFToolkit.Extended assembly.</summary>public static class Licenser{    private static DependencyObject DesignModeReferenceObject = new DependencyObject();    private static XceedLicense _license;    private static bool m_fullyUnlock;    private static string _licenseKey = string.Empty;    static Licenser()    {        // if (DesignerProperties.GetIsInDesignMode(Licenser.DesignModeReferenceObject))        // {        //     Licenser.Unlock();        // }    }    internal static XceedLicense License => _license;    /// <summary>    /// Gets or sets the license key.    /// </summary>    public static string LicenseKey    {        get        {            return _licenseKey;        }        set        {            ArgumentNullException.ThrowIfNullOrEmpty(value, nameof(LicenseKey));            if (_license != null && _license.IsFromRegistry)            {                ThrowException.ThrowLicenseException(null, null, typeof(Licenser).FullName + ".LicenseKey must be set to a valid license key in your code before the product is used.  Please refer to the Licensing topic in the documentation for specific instructions on how to avoid this exception.");            }            _license = ToolkitLicense.CreateLicense(value, typeof(Licenser));            _licenseKey = value;        }    }    internal static void Unlock()    {        m_fullyUnlock = true;    }    internal static void VerifyLicense()    {        if (!m_fullyUnlock)        {            if (_license == null)            {                _license = ToolkitLicense.CreateLicense(string.Empty, typeof(Licenser));            }        }    }    internal static void VerifyLicense(FeatureID featureId)    {        if (!m_fullyUnlock)        {            VerifyLicense();        }    }    internal static void VerifyLicenseDesignMode()    {        if (_license == null)        {            try            {                VerifyLicense();            }            catch            {            }        }    }    internal static void VerifyLicenseDesignMode(FeatureID featureID)    {        try        {            VerifyLicense(featureID);        }        catch        {        }    }}